import React, { useState, useContext } from "react";
import { Link, Outlet } from "react-router-dom";
import styled from "styled-components";
import AuthContext from "./AuthContext";

const Navbar = () => {

  let {auth, logoutUser} = useContext(AuthContext)
  return (
    <>
      <nav className="lg:flex  bg-[#e06377] items-center justify-between lg:py-1 lg:pl-4">
        <Link to="/" className="text-2xl font-semibold pt-8 px-2 text-3xl ">
          MEMOS
        </Link>
        <ul className="flex flex-col">
          <li className="flex font-bold flex-col lg:flex-row">
          {<>
            <Link to="/home" className="  border border-y-red-900">
              <div className="px-6  rounded-md  py-3  hover:bg-[#c83349]">
                Home
              </div>
            </Link>
            <Link to="/gallery">
              <div className="px-6 py-3  rounded-md hover:bg-[#c83349]">
                Gallery
              </div>
            </Link>

            <Link to="/profile">
              <div className="px-6 py-3  rounded-md hover:bg-[#c83349]">
                Profile
              </div>
            </Link>
            </>
            }
            {auth ?
            <Link to="/" onClick={logoutUser}>
              <div className="px-6 py-3  rounded-md hover:bg-[#c83349]">
                Logout
              </div>
            </Link>
            :
            <Link to="/accounts/login">
              <div className="px-6 py-3  rounded-md hover:bg-[#c83349]">
                Login
              </div>
            </Link>}
          </li>
        </ul>
      </nav>
      <Outlet />
      
    </>
  );
};
{
  /*}
const NavLink = styled(Link)`
  padding: 20px;
  color: black;
  text-decoration: none;
  &:hover {
    color: blue;
  }
`;
*/
}

export default Navbar;
